import React, { useState, useCallback, useRef, useEffect } from 'react';
import { motion } from 'framer-motion';
import Webcam from 'react-webcam';
import axios from 'axios';

const GroupAnalysis: React.FC = () => {
  const [mode, setMode] = useState<'analysis' | 'verification'>('analysis');
  const [imageFile, setImageFile] = useState<File | null>(null);
  const [results, setResults] = useState<any>(null);
  const [isProcessing, setIsProcessing] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const webcamRef = useRef<Webcam | null>(null);
  const [isCameraActive, setIsCameraActive] = useState(false);
  const [requiredUsers, setRequiredUsers] = useState<string[]>([]);
  const [registeredUsers, setRegisteredUsers] = useState<string[]>([]);

  // Fetch registered users when component mounts
  useEffect(() => {
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5001/api/registered-users');
        if (response.data.success) {
          setRegisteredUsers(response.data.users);
        }
      } catch (err) {
        console.error('Failed to fetch registered users:', err);
      }
    };
    fetchUsers();
  }, []);

  const handleFileChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setImageFile(file);
      setError(null);
    }
  };

  const captureImage = useCallback(() => {
    if (webcamRef.current) {
      const imageSrc = webcamRef.current.getScreenshot();
      if (imageSrc) {
        // Convert base64 to blob
        fetch(imageSrc)
          .then(res => res.blob())
          .then(blob => {
            const file = new File([blob], "webcam-capture.jpg", { type: "image/jpeg" });
            setImageFile(file);
          });
      }
    }
  }, [webcamRef]);

  const handleSubmit = async () => {
    if (!imageFile) {
      setError('Please select an image or capture one from the camera');
      return;
    }

    if (mode === 'verification' && requiredUsers.length === 0) {
      setError('Please select at least one required user for verification');
      return;
    }

    setIsProcessing(true);
    setError(null);

    const formData = new FormData();
    formData.append('image', imageFile);

    if (mode === 'verification') {
      requiredUsers.forEach(user => {
        formData.append('required_users', user);
      });
    }

    try {
      const endpoint = mode === 'analysis' 
        ? 'http://localhost:5001/api/group-analysis' 
        : 'http://localhost:5001/api/verify-group';

      const response = await axios.post(endpoint, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.success) {
        setResults(response.data);
      } else {
        setError(response.data.message || 'An error occurred');
      }
    } catch (err) {
      console.error('Error:', err);
      setError('Failed to process the request. Please try again.');
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      exit={{ opacity: 0 }}
      className="min-h-screen bg-gray-100 py-12 px-4 sm:px-6 lg:px-8"
    >
      <div className="max-w-4xl mx-auto">
        <div className="bg-white rounded-lg shadow-lg p-6">
          <h1 className="text-3xl font-bold text-gray-900 mb-8">Group Analysis</h1>

          {/* Mode Selection */}
          <div className="mb-8">
            <div className="flex space-x-4">
              <button
                onClick={() => setMode('analysis')}
                className={`px-4 py-2 rounded-md ${
                  mode === 'analysis'
                    ? 'bg-indigo-600 text-white'
                    : 'bg-gray-200 text-gray-700'
                }`}
              >
                Analysis Mode
              </button>
              <button
                onClick={() => setMode('verification')}
                className={`px-4 py-2 rounded-md ${
                  mode === 'verification'
                    ? 'bg-indigo-600 text-white'
                    : 'bg-gray-200 text-gray-700'
                }`}
              >
                Verification Mode
              </button>
            </div>
          </div>

          {/* Input Methods */}
          <div className="mb-8">
            <div className="flex flex-col space-y-4">
              {/* File Upload */}
              <div>
                <label className="block text-sm font-medium text-gray-700">
                  Upload Image
                </label>
                <input
                  type="file"
                  accept="image/*"
                  onChange={handleFileChange}
                  className="mt-1 block w-full text-sm text-gray-500
                    file:mr-4 file:py-2 file:px-4
                    file:rounded-md file:border-0
                    file:text-sm file:font-semibold
                    file:bg-indigo-50 file:text-indigo-700
                    hover:file:bg-indigo-100"
                />
              </div>

              {/* Camera Toggle */}
              <div>
                <button
                  onClick={() => setIsCameraActive(!isCameraActive)}
                  className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700"
                >
                  {isCameraActive ? 'Disable Camera' : 'Enable Camera'}
                </button>
              </div>

              {/* Webcam */}
              {isCameraActive && (
                <div className="mt-4">
                  <Webcam
                    ref={webcamRef}
                    audio={false}
                    screenshotFormat="image/jpeg"
                    className="w-full max-w-md mx-auto rounded-lg shadow-lg"
                  />
                  <button
                    onClick={captureImage}
                    className="mt-4 inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700"
                  >
                    Capture Image
                  </button>
                </div>
              )}
            </div>
          </div>

          {/* Required Users */}
          {mode === 'verification' && (
            <div className="mb-8">
              <label className="block text-sm font-medium text-gray-700">
                Select Required Users
              </label>
              <select
                multiple
                value={requiredUsers}
                onChange={(e) => {
                  const selectedUsers = Array.from(e.target.selectedOptions, (option) => option.value);
                  setRequiredUsers(selectedUsers);
                }}
                className="mt-1 block w-full text-sm text-gray-500"
              >
                {registeredUsers.map((user) => (
                  <option key={user} value={user}>
                    {user}
                  </option>
                ))}
              </select>
            </div>
          )}

          {/* Submit Button */}
          <div className="mt-6">
            <button
              onClick={handleSubmit}
              disabled={isProcessing || !imageFile}
              className={`w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white ${
                isProcessing || !imageFile
                  ? 'bg-indigo-400 cursor-not-allowed'
                  : 'bg-indigo-600 hover:bg-indigo-700'
              }`}
            >
              {isProcessing ? 'Processing...' : 'Analyze Group'}
            </button>
          </div>

          {/* Error Message */}
          {error && (
            <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-md">
              <p className="text-sm text-red-600">{error}</p>
            </div>
          )}

          {/* Results */}
          {results && (
            <div className="mt-8 p-4 bg-gray-50 rounded-lg">
              <h3 className="text-lg font-medium text-gray-900 mb-4">Results</h3>
              <pre className="whitespace-pre-wrap text-sm text-gray-700">
                {JSON.stringify(results, null, 2)}
              </pre>
            </div>
          )}
        </div>
      </div>
    </motion.div>
  );
};

export default GroupAnalysis;
